if (numDevices > 1)
{
    for (var idevice = 1; idevice < numDevices; ++idevice)
    {
        var deviceAlreadyFound = false;
        for (ii = 0; ii < idevice; ++ii)
        {
            if (msg.payload[idevice].deviceType == deviceList[ii]) deviceAlreadyFound = true;
        }
        if (!deviceAlreadyFound) deviceList[idevice] = msg.payload[idevice].deviceType;
    }
}

return {
