}
 function attrSelectedSpecial(itrace)
 {
     $('#csv'+ itrace.toString()).removeAttr("href");
     $('#csv'+ itrace.toString()).css('color', 'black');
     $('#csv'+ itrace.toString()).css('text-decoration', 'none');
     $('#csv'+ itrace.toString()).css('font-weight', 'bold');
 }
 function selectAxis(itrace,axis)
 {
     devInfo[itrace].axis  = axis;
 }
 function selectPts(itrace)
 {
     devInfo[itrace].pts = !devInfo[itrace].pts;
 }
 function getArchiveData()
 {
     archiveRequestList = [];
     archiveList = [];
     startDate = new Date($( "#startDate" ).val()).getTime();
     stopDate = new Date($( "#stopDate" ).val()).getTime();
     var traceCount = 0;
     for (var itrace = 0; itrace < numTraces; ++itrace)
     {
         if (devInfo[itrace].name.length > 0) 
         {
             archiveRequestList[traceCount] = 
             {
                 deviceType  : devInfo[itrace].deviceType, 
                 name        : devInfo[itrace].name, 
                 prop        : devInfo[itrace].prop, 
                 attr        : devInfo[itrace].attr,
                 startDate   : startDate, 
                 stopDate    : stopDate,
                 itrace      : itrace
             };
             ++traceCount;
         }
     }
     if (traceCount < 1) return;
     $('#plotSetupTable').hide();
     $('#timePlotCard').hide();
     for (var itrace = 0; itrace < numTraces; ++itrace)
     {
         $('#csv'+ itrace.toString()).removeAttr("href");
         $('#csv'+ itrace.toString()).css('color', 'black');
         $('#csv'+ itrace.toString()).css('text-decoration', 'none');
         $('#csv'+ itrace.toString()).css('font-weight', 'bold');
     }
     messageDialog('Alert', 'Wait!', 'Requesting data ...');
     getArchiveValues();

 }
 function getArchiveValues()
 {
     var maxPtsToPlot = Number($( "#maxPtsToPlot" ).val());
     var actionMsg = 
         {
             device      : archiveRequestList[0],
             maxPtsToPlot: maxPtsToPlot
         };
     sendActionMsg('getArchiveValues', 'readDatabase', actionMsg);
     archiveRequestList.shift();
     return;
 }
 function loadArchiveValues()
 {
     $( "#messageDialog" ).dialog('close');
     $('#plotSetupTable').show();
     var plotTraces = [];
     for (ii = 0; ii < archiveList.length; ++ii)
     {
         itrace = archiveList[ii].itrace;
         var modeType = 'lines';
         if (devInfo[itrace].pts) modeType = 'markers';
         var trace =  
         {
             x: [],
             y: [],
             name: devInfo[itrace].axis + ' ' + devInfo[itrace].deviceType + '.' + devInfo[itrace].name + '.' + devInfo[itrace].prop + '.' + devInfo[itrace].attr + ' (' + devInfo[itrace].device[devInfo[itrace].attr].unit + ')',
             yaxis: devInfo[itrace].axis,
             type: 'scatter',
             mode: modeType
         }
         for (var ipt = 0; ipt < archiveList[ii].trace.length; ++ipt)
         {
             trace.x[ipt] = archiveList[ii].trace[ipt].timeStamp;
             trace.y[ipt] = archiveList[ii].trace[ipt].value;
         }
         plotTraces.push(trace);
         makeCsvFile(ii);
     }
     var plotLayout =
     {
         showlegend: true,
         legend: {x: 0,y: 1.35},
         xaxis       : {type: 'date' },
         yaxis: {title: 'y1'},
         yaxis2: {title: 'y2',overlaying: 'y',side: 'right'}
     };
     $('#timePlotCard').show();
     Plotly.newPlot('timePlot', plotTraces, plotLayout);
 }
 function makeCsvFile(ii)
 {
     var dataString = '';
     var startT = archiveList[ii].trace[0].timeStamp;
     var itrace = archiveList[ii].itrace;

     dataString = dataString + 'Device,' + devInfo[itrace].deviceType + ',' + devInfo[itrace].name + ',' + devInfo[itrace].prop + ',' + devInfo[itrace].attr  +'\n';
     dataString = dataString + 'StartDate,' + new Date(startT).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}) + '\n';
     dataString = dataString + 'StartDate (mS),' + startT.toString() + '\n';
     dataString = dataString + 'Time (sec), Value (' + devInfo[itrace].device[devInfo[itrace].attr].unit + ')\n';
     for (var ipt = 0; ipt < archiveList[ii].trace.length; ++ipt)
     {
         var x = (archiveList[ii].trace[ipt].timeStamp - startT) / 1000;
         var y = archiveList[ii].trace[ipt].value;
         dataString = dataString + x.toString() + ',' + y.toString() + '\n';
     }
     var data = new Blob([dataString], {type: 'text/plain'});

     // If we are replacing a previously generated file we need to
     // manually revoke the object URL to avoid memory leaks.
     if (csvFile[itrace] !== null) {
       window.URL.revokeObjectURL(csvFile[itrace]);
     }

     csvFile[itrace] = window.URL.createObjectURL(data);
     // returns a URL you can use as a href
     $('#csv'+ itrace.toString()).css('color', 'blue');
     $('#csv'+ itrace.toString()).css('text-decoration', 'underline');
     $('#csv'+ itrace.toString()).css('font-weight', 'bold');
     $('#csv'+ itrace.toString()).attr("href", csvFile[itrace]);
     $('#csv'+ itrace.toString()).attr("download", devInfo[itrace].deviceType + '-' + devInfo[itrace].name + '-' + devInfo[itrace].prop + '-' + devInfo[itrace].attr + '.csv');

 }

func:
var projectionFilter =  
{
    projection:
    {
        _id                         :   0, 
        deviceType                  :   1,  
        name                        :   1,  
        timeStamp                   :   1, 
        [msg.payload.device.attr]     :   1
    } 
};
var queryFilter = 
{
    $and :
    [
        {
            deviceType: msg.payload.device.deviceType
        },
        {
            name: msg.payload.device.name
        },
        {
            timeStamp :
            {
                $gte: msg.payload.device.startDate,
                $lte: msg.payload.device.stopDate
            }
        }
    ] 

}
var newMsg = 
{
    topic           : 'getArchiveValues',
    userID          : msg.userID,
    payload         : [queryFilter, projectionFilter],
    device          : msg.payload.device,
    maxPtsToPlot    : msg.payload.maxPtsToPlot,
    itrace          : msg.payload.device.itrace
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? -1 : ((x > y) ? 1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic:'getArchiveValues',
        payload:{
            topic           : 'getArchiveValues',
            payload         : {itrace: msg.itrace, trace:null},
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) records[ii] = msg.payload[ii]; 
sortByKey(records, 'timeStamp');
var trace = [];
var maxPts = msg.maxPtsToPlot;
var nstep = numRecords / msg.maxPtsToPlot;
if (nstep < 1.0) 
{
    nstep = 1.0;
    maxPts = numRecords;
}
var icnt = 0;

while(icnt < maxPts)
{
    var index = Math.round(icnt * nstep);
    trace[icnt] = {timeStamp: records[index].timeStamp, value: records[index][msg.device.attr]};
    ++icnt
}

return {
    topic:'getArchiveValues',
    payload:{
        topic           : 'getArchiveValues',
        payload         : {itrace: msg.itrace, trace:trace},
        userID          : msg.userID,
    }
};

template:
var numTraces = 4;
var deviceSelect = [];
var nameSelect = [];
var propSelect=[];
var attrSelect=[];
var valSelect=[];
var devInfo = [];
var permitGetDeviceValues = true;


useWebSockets(true);
function onDocumentReady()
{
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        deviceSelect[itrace] = document.getElementById('deviceSelect' + itrace.toString());
        nameSelect[itrace] = document.getElementById('nameSelect' + itrace.toString());
        propSelect[itrace] = document.getElementById('propSelect' + itrace.toString());
        attrSelect[itrace] = document.getElementById('attrSelect' + itrace.toString());
        valSelect[itrace] = document.getElementById('valSelect' + itrace.toString());
        devInfo[itrace] = {deviceType: '', name : '',  prop : '',attr : '',device : {}, axis:'y1', pts:true};
        $('#unitSelect'+ itrace.toString()).html('');
    }
    setInterval(getDeviceValues, 1000);
    onDocumentReadySpecial();
}
function onWebSocketMessage(msg)
{
    if (msg.userID == userID)
    {
        switch(msg.topic)
        {
            case 'getDeviceList':
                loadDeviceList(msg.payload);
                break;
            case 'getNameList':
                loadNameList(msg);
                break;
            case 'getAttrList':
                loadAttrList(msg);
                break;
            case 'getDeviceValues':
                loadDeviceValues(msg);
                break;
            case 'permissionError':
                acknowledgeDialog('Permission', 'Error', msg.payload);
                break;
            case 'loginExpired':
                location.reload();
                break;
            default:
                onWebSocketMessageSpecial(msg);
        }
    }
 }
function onWebSocketOpen()
{
    var actionMsg = 
        {
        };
    sendActionMsg('getDeviceList', 'readDatabase', actionMsg);
}
function onWebSocketClose()
{

}
function loadDeviceList(deviceList)
{
    deviceList.sort();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        for (var ii = 0; ii < deviceList.length; ++ii)
        {
            var opt = document.createElement('option');
            opt.value = deviceList[ii];
            opt.innerHTML = deviceList[ii];
            deviceSelect[itrace].appendChild(opt);
        }
    }
}
function loadNameList(data)
{
    data.nameList.sort();
    for (var ii = 0; ii < data.nameList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.nameList[ii];
        opt.innerHTML = data.nameList[ii];
        nameSelect[data.trace].appendChild(opt);
    }

    return;
}
function loadAttrList(data)
{
    data.attrList.sort();
    for (var ii = 0; ii < data.attrList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.attrList[ii];
        opt.innerHTML = data.attrList[ii];
        attrSelect[data.trace].appendChild(opt);
    }
}
function deviceSelected(itrace)
{
    clearNameOptionList(itrace);
    if (deviceSelect[itrace].value == 'notSelected') return;
    nameSelect[itrace].value = 'notSelected';
    var actionMsg = 
        {
            trace       : itrace,
            deviceType  : deviceSelect[itrace].value
        };
    sendActionMsg('getNameList', 'readDatabase', actionMsg);
}
function nameSelected(itrace)
{
    clearPropOptionList(itrace);
    if (nameSelect[itrace].value == 'notSelected') return;
    var readOpt = document.createElement('option');
    readOpt.value = 'reading';
    readOpt.innerHTML = 'reading';
    propSelect[itrace].appendChild(readOpt);
    var setOpt = document.createElement('option');
    setOpt.value = 'setting';
    setOpt.innerHTML = 'setting';
    propSelect[itrace].appendChild(setOpt);

    propSelect[itrace].value = 'notSelected';
}
function propSelected(itrace)
{
    clearAttrOptionList(itrace);
    if (propSelect[itrace].value == 'notSelected') return;
    var actionMsg = 
        {
            trace       : itrace,
            deviceType  : deviceSelect[itrace].value,
            name        : nameSelect[itrace].value,
            prop        : propSelect[itrace].value
        };
    sendActionMsg('getAttrList', 'readDatabase', actionMsg);
}
function attrSelected(itrace)
{
    devInfo[itrace].deviceType = '';
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};
    if (attrSelect[itrace].value == 'notSelected') 
    {
        $('#valueSelect' + itrace.toString()).html('');
        $('#unitSelect' + itrace.toString()).html('');
        $('#editDeviceButton'+ itrace.toString()).hide();

        return;
    }
    devInfo[itrace].deviceType = deviceSelect[itrace].value;
    devInfo[itrace].name = nameSelect[itrace].value;
    devInfo[itrace].prop = propSelect[itrace].value;
    devInfo[itrace].attr = attrSelect[itrace].value;
    attrSelectedSpecial(itrace);

}
function clearNameOptionList(itrace)
{
    devInfo[itrace].deviceType = '';
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};

    while (nameSelect[itrace].firstChild)
    {
        nameSelect[itrace].removeChild(nameSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    nameSelect[itrace].appendChild(opt);
    clearPropOptionList(itrace);
}
function clearPropOptionList(itrace)
{
    devInfo[itrace].deviceType = '';
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};

    while (propSelect[itrace].firstChild)
    {
        propSelect[itrace].removeChild(propSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    propSelect[itrace].appendChild(opt);
    clearAttrOptionList(itrace);
}
function clearAttrOptionList(itrace)
{
    devInfo[itrace].deviceType = '';
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};

    while (attrSelect[itrace].firstChild)
    {
        attrSelect[itrace].removeChild(attrSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    attrSelect[itrace].appendChild(opt);
    $('#unitSelect'+ itrace.toString()).html('');
    $('#valueSelect' + itrace.toString()).html('');
    clearAttrOptionListSpecial(itrace);
}
function getDeviceValues()
{
    var devList = [];
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if ((devInfo[itrace].deviceType.length > 0) && (devInfo[itrace].name.length > 0)) 
        {
            devList[traceCount] = { $and: [ {deviceType : devInfo[itrace].deviceType}, {name : devInfo[itrace].name}]};
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    var queryFilter = { $or: devList };
    var actionMsg = 
        {
            queryFilter : queryFilter
        };
    sendActionMsg('getDeviceValues', 'readDatabase', actionMsg);
}
function loadDeviceValues(data)
{
    if (!permitGetDeviceValues) return;
    var devices = data.devices;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].attr.length > 0)
        {
            for (idev = 0; idev < devices.length; ++idev)
            {
                if (devices[idev].deviceType == devInfo[itrace].deviceType)
                {
                    if(devices[idev].name == devInfo[itrace].name)
                    {
                        devInfo[itrace].device = devices[idev];
                        var attr = devInfo[itrace].device[devInfo[itrace].attr];
                        var valueColor = 'black';
                        if (attr.alarm.value > 0) valueColor = 'orange';
                        if (attr.alarm.value > 2) valueColor = 'red';
                        $('#valueSelect' + itrace.toString()).css("color", valueColor);
                        $('#valueSelect' + itrace.toString()).html(attr.value.toString());
                        $('#unitSelect' + itrace.toString()).css("color", valueColor);
                        $('#unitSelect' + itrace.toString()).html(attr.unit);
                    }
                }
            } 
        }
    }
    loadDeviceValuesSpecial(data);
}

