        useWebSockets(true);
        function onDocumentReady()
        {
            getCreds();
            setInterval(function(){getCreds();}, 1000);
        }
        function onWebSocketOpen()
        {
        }
        function onWebSocketClose()
        {

        }
        function onWebSocketMessage(msg)
        {
            switch(msg.topic)
            {
                case 'renew':
                    if (msg.userID == userID)
                    {
                        updateCookie(msg);
                    }
                    break;
                case 'permissionError':
                    if (msg.userID == userID)
                    {
                        acknowledgeDialog('Permission', 'Error', msg.payload);
                    }
                    break;
                case 'loginExpired':
                    if (msg.userID == userID)
                    {
                       location.reload();
                    }
                    break;
                default:
                    break;
            }
        }
        function launchApp()
        {
            var application = $('#application').val();
            var devices = [];
            if ($('#traceA').val() != 'none') devices.push($('#traceA').val());
            if ($('#traceB').val() != 'none') devices.push($('#traceB').val());
            if ($('#traceC').val() != 'none') devices.push($('#traceC').val());
            if ($('#traceD').val() != 'none') devices.push($('#traceD').val());
            if (devices.length < 1)
            {
                acknowledgeDialog('Acknowledge', 'Error', 'At least one trace must have a valid device');
                return;
            }
            var link = "/" + application + '?name=' + devices[0];
            if (devices.length > 1)
            for (var ii = 1; ii < devices.length; ++ii)
            {
                link = link + ',' + devices[ii];
            }
//            window.location.href = link;
            window.open(link, '_blank');
        }
        function getCreds()
        {
            var now = new Date().getTime();
            var expDate = Number(getCookie('ExpDate'));
            if (expDate > 0)
            {
                expDate = expDate - now;
                if(expDate < 0)
                {
                    logout();
                }
                else
                {
                    var hours = Math.floor(expDate / 3600000);
                    var minutes = expDate - hours * 3600000;
                    minutes = Math.floor(minutes / 60000);
                    var seconds = expDate - (hours * 3600000) - (minutes * 60000);
                    seconds = Math.floor(seconds / 1000);
                    hours = hours.toString();
                    if (hours.length == 1) hours = '0' + hours; 
                    minutes = minutes.toString();
                    if (minutes.length == 1) minutes = '0' + minutes; 
                    seconds = seconds.toString();
                    if (seconds.length == 1) seconds = '0' + seconds; 
                    var timeString = hours + ':' + minutes + ':' + seconds;
                    $('#usernameId').text(getCookie('Username'));
                    $('#expTimeId').text(timeString);
                }
            }
            else
            {
                $('#usernameId').text(getCookie('Username'));
                $('#expTimeId').text('Forever');
            }
        }
        function logout()
        {
            document.cookie = project + "Role= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
            document.cookie = project + "ExpDate= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
            document.cookie = project + "Username= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
            window.location.href = "/";
        }
        function renew()
        {
            var actionMsg = {};
            sendActionMsg('renew', 'renew', actionMsg);
        }
        function updateCookie(msg)
        {
            document.cookie = project + "Role=" + msg.role;
            document.cookie = project + "ExpDate=" + msg.expDate;
            document.cookie = project + "Username=" + msg.username;
        }
        function downloadToken()
        {
            downloadString(getCookie("Role"), "text/plain", project + "-token.txt");
        }
// from https://gist.github.com/danallison/3ec9d5314788b337b682
        function downloadString(text, fileType, fileName) 
        {
            var blob = new Blob([text], { type: fileType });

            var a = document.createElement('a');
            a.download = fileName;
            a.href = URL.createObjectURL(blob);
            a.dataset.downloadurl = [fileType, a.download, a.href].join(':');
            a.style.display = "none";
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
            setTimeout(function() { URL.revokeObjectURL(a.href); }, 1500);
        }

