                yaxis: 'y2',
                type: 'scatter',
                mode: 'markers',
                marker: {
                    color: this.plotConfig.trace2Color,
                    size: 10,
                    symbol: 'square'
                }        

            };
            for (var ii = 0; ii < archiveList[0].trace.length; ++ii)
            {
//                var x = (archiveList[0].trace[ii].timeStamp - archiveList[0].device.stopDate) / 3600000.0;
                var x = archiveList[0].trace[ii].timeStamp;
                trace1.x[ii] = x;
                trace1.y[ii] = archiveList[0].trace[ii][archiveList[0].device.attr[0]] ;
                trace2.x[ii] = x;
                trace2.y[ii] = archiveList[0].trace[ii][archiveList[0].device.attr[1]];
            }


            Plotly.newPlot('archiveChart', [trace1,trace2], layout);
            $('#archive2hrButton').attr("disabled", false); 
            $('#archive4hrButton').attr("disabled", false); 
            $('#archive8hrButton').attr("disabled", false); 
            $('#archive24hrButton').attr("disabled", false); 
            $('#archive72hrButton').attr("disabled", false); 
            $('#archive168hrButton').attr("disabled", false); 
        }

