var mqttArray = msg.topic.split('/');
var device = 
{
    project     : mqttArray[0],
    deviceType  : mqttArray[1],
    name        : mqttArray[2],
    prop        : mqttArray[3],
};
return {topic : msg.topic, payload:{topic:'readings', mqttDevice : device, payload:msg.payload}};

var projectionFilter =  
{
    projection:
    {
        _id                         :   0, 
        deviceType                  :   1,  
        name                        :   1,  
        timeStamp                   :   1 
    } 
};
for (var ii = 0; ii < msg.payload.device.attr.length; ++ii)
{
    projectionFilter.projection[msg.payload.device.attr[ii]] = 1;
}
var queryFilter = 
{
    $and :
    [
        {
            deviceType: msg.payload.device.deviceType
        },
        {
            name: msg.payload.device.name
        },
        {
            timeStamp :
            {
                $gte: msg.payload.device.startDate,
                $lte: msg.payload.device.stopDate
            }
        }
    ] 

}
var newMsg = 
{
    topic           : msg.topic,
    userID          : msg.userID,
    payload         : [queryFilter, projectionFilter],
    device          : msg.payload.device,
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? -1 : ((x > y) ? 1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic : msg.device.deviceType,
        payload:{
            topic           : 'getArchiveValues',
            payload         : {device: msg.device, trace:null},
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) records[ii] = msg.payload[ii]; 
sortByKey(records, 'timeStamp');
var trace = [];
for (var ii = 0; ii < numRecords; ++ii)
{
    trace[ii] = {timeStamp: records[ii].timeStamp};
    for (var ij = 0; ij < msg.device.attr.length; ++ij)
    {
        trace[ii][msg.device.attr[ij]] = records[ii][msg.device.attr[ij]];
    }
}

return {
    topic : msg.device.deviceType,
    payload:{
        topic           : msg.topic,
        payload         : {device: msg.device, trace: trace},
        userID          : msg.userID,
    }
};

var newMsg = msg.payload;
newMsg.userID = msg.userID
return newMsg;

var newMsg = msg.payload;
newMsg.userID = msg.userID
return newMsg;

var errMsg = null;
if (msg.token.expDate > 0)
{
    if (msg.token.expDate < new Date().getTime())
    {
        errMsg = 
        {
            topic : msg.topic, 
            payload:
            {
                topic:'loginExpired', 
                payload: msg.topic + " login expired",
                userID : msg.userID,
                role   : msg.role
            }
        };
        return [null,errMsg];
    }
}
permittedRole = false;
for (itoken = 0; itoken < msg.token.roles.length; ++itoken)
{
    if (msg.token.roles[itoken] == msg.role) permittedRole = true;
}
if (permittedRole) return [msg,null];

errMsg = 
{
    topic : msg.topic, 
    payload:
    {
        topic  : 'permissionError', 
        payload: msg.topic + " not permitted",
        userID : msg.userID,
        role   : msg.role
    }
};

return [null,errMsg];

